package edu.handong.csee.java.Lab09;

public class Salesman {//a class.

	private String mName; // string.
	private double sales; // double.

	public Salesman(){//a method.
		mName = ""; //initialization.
		sales = 0;//initialization.
	}

	public void Salesman(String mName, double sales) { // a method.
		this.mName = mName; //use this.
		this.sales = sales;//use this.
	}
	public void setmName(String mName) {//a method.
		// TODO Auto-generated method stub
		this.mName = mName;//use this.
	}

	public void setSales(double Sales) {//a method.
		// TODO Auto-generated method stub
		this.sales = Sales;//use this.
	}
	public double getSales() {//a method.
		// TODO Auto-generated method stub
		return sales = 0;//use this.
	}

	public String getmName() {//a method.
		// TODO Auto-generated method stub
		return mName = "";//use this.
	}
}
